import android.location.Address;
import android.location.Criteria;
import android.location.Geocoder;
import android.location.Location;
import android.location.LocationListener;
import android.location.LocationManager;
import android.os.AsyncTask;
import android.os.Bundle;
import android.telephony.ServiceState;
import android.telephony.TelephonyManager;
import android.util.Log;

public class MainActivity extends Activity {
    /** Called when the activity is first created. */
    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.splash);
        
        ProgressDialog mProgressDialog;
        mProgressDialog = new ProgressDialog(this);
        mProgressDialog.setMessage("Baixando informações sobre sua operadora e localização, aguarde alguns segundos.");
        mProgressDialog.setIndeterminate(false);
        mProgressDialog.setMax(100);
        mProgressDialog.setProgressStyle(ProgressDialog.STYLE_SPINNER);
        
        SharedPreferences settings = getSharedPreferences("PreferencesFile", MODE_PRIVATE);        
        StringDownloaderTask sdt = new StringDownloaderTask(this, mProgressDialog);
        sdt.execute(settings);      
    }    
}

class StringDownloaderTask extends AsyncTask<SharedPreferences, Void, String>{
	Activity ac1;
	LocationListener ll;
	double longitude;
	double latitude;
	LocationManager lm;
	ProgressDialog pdInternal;	
	
	public StringDownloaderTask(Activity ac, ProgressDialog pd){
		ac1 = ac;
		pdInternal = pd;
		lm = (LocationManager) ac1.getSystemService(Context.LOCATION_SERVICE);
		longitude = 0;
		latitude = 0;		
	}
	
	@Override
	protected String doInBackground(SharedPreferences... params){
		// params comes from the execute() call: params[0] is the url.
		String line1 = null;
		String line2,line3;
		String city = null;
		String estate = null;		
		String url = "http://www.kubic.com.br/daisho/speed.html";		
		SharedPreferences set = (SharedPreferences) params[0];
		SharedPreferences.Editor prefEditor = set.edit();		
		
		try{
			TelephonyManager tm = (TelephonyManager) ac1.getSystemService(Context.TELEPHONY_SERVICE);
			if (tm!=null){
		        String name = tm.getNetworkOperator();
		        int network = tm.getNetworkType();	
		        prefEditor.putString("operator", name);
		        prefEditor.putInt("connection", network);
			}
	        		        
	        Criteria crit = new Criteria();
	        crit.setAccuracy(Criteria.NO_REQUIREMENT);
	        String provider = lm.getBestProvider(crit,true);	        
	        
	        Location loc = lm.getLastKnownLocation(provider); 
	        if (loc!=null){	        	
		        longitude = loc.getLongitude();
		        latitude = loc.getLatitude();
	        }	        
	        
	        List<Address> ad = null;
	        Geocoder gcd = new Geocoder(ac1,Locale.getDefault());
	        try {
				ad = gcd.getFromLocation(latitude,longitude,1);
			} catch (IOException e) {			
				e.printStackTrace();
			}
	        String location = "";
	        if (ad.size()>0)
	        	location = ad.get(0).getAddressLine(1);
	        int index = location.indexOf('-');
	        if (index!=-1){
		        city = location.substring(0,index-1);
		        estate = location.substring(index+2);
		        prefEditor.putString("city", city);
		        prefEditor.putString("estate", estate);
	        }	                	        
			
			//HttpParams httpParameters = new BasicHttpParams();
			//HttpConnectionParams.setConnectionTimeout(httpParameters,5000);
			HttpClient httpclient = new DefaultHttpClient();			
			HttpResponse response = httpclient.execute(new HttpGet(url));
			BufferedReader reader = new BufferedReader(new InputStreamReader(response.getEntity().getContent()));
	        line1 = reader.readLine();
			line2 = reader.readLine();
			line3 = reader.readLine();			
			
			prefEditor.putString("countrydata", line1);
			prefEditor.putString("estatedata", line2);
			prefEditor.putString("citydata", line3);					
        	prefEditor.commit();
        	
		}		
		catch(Exception e){
			e.printStackTrace();
		}
		return line1;
	}
	
	protected void onPreExecute(){
		super.onPreExecute();
		pdInternal.show();
	}
	
	protected void onPostExecute(String s){		
		pdInternal.dismiss();
		Intent intent = new Intent(ac1,br.com.kubic.testedevelocidade.MainActivity2.class);
    	ac1.startActivity(intent);
	}
}


