package br.com.kubic.testedevelocidade;


import android.app.Activity;
import android.content.Intent;
import android.content.SharedPreferences;
import android.os.Bundle;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.ListView;
import android.widget.TextView;
import android.widget.Toast;

public class MainActivity2 extends Activity {
	boolean failOperator;
	boolean failCity;
    /** Called when the activity is first created. */
    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.main);
        
        SharedPreferences settings = getSharedPreferences("PreferencesFile", MODE_PRIVATE);
        String city = settings.getString("city", "");
        String estate = settings.getString("estate","");
        String operator = settings.getString("operator", "");
        int connection = settings.getInt("connection", 0);
        String con = null;
        String ope = null;
        failOperator = false;     
        if (city.equals(""))
        	failCity = true;
        else
        	failCity = false;
        
        switch(connection){
        	case 8:
        			con = "HSDPA";
        			break;
        	case 10:
        			con = "HSPA";
        			break;
        	case 15: 
        			con = "HSPA+";
        			break;
        	case 2:
        			con = "EDGE";
        			break;
        	case 5:case 6:case 12:
        			con = "EVDO";
        			break;
        	default:
        			con = "3G";
        }
        
        /*convert operator code to name*/
        if (operator.length()==5){
        	int value = Integer.parseInt(operator.substring(3));
        	switch(value){
	        	case 2:case 3:case 4:case 8: ope = "TIM";
	        						break;
	        	case 10:case 6:case 11:case 23: ope = "VIVO";
	        			break;
	        	case 5: ope = "CLARO";
	        				break;
	        	case 18:case 31:case 16:case 24: ope = "OI";
	        			break;
	        	default: 
	        			ope = "Não Identificado";
	        			failOperator = true;
        	}
        }
        else{
        	failOperator = true;
        }
        SharedPreferences.Editor prefEditor = settings.edit();        
        prefEditor.putString("ope", ope);
        prefEditor.commit();  
        
        TextView tv1 = (TextView)findViewById(R.id.tv1);
        TextView tv2 = (TextView)findViewById(R.id.tv2);
        TextView tv3 = (TextView)findViewById(R.id.tv3);
        TextView tv4 = (TextView)findViewById(R.id.tv4);        
        
        tv1.setText("Operadora: "+ope);
        tv2.setText("Conexão: "+con);
        tv3.setText("Cidade: "+city);
        tv4.setText("Estado: "+estate);
        
    }
    
    public void startTest(View v){
    	if (failOperator){
    		Toast.makeText(this, "Operadora não identificada. Verifique se o seu SIM está funcionando corretamente.",Toast.LENGTH_LONG).show();
    	}
    	else if(failCity){
    		Toast.makeText(this, "Localização não válida. Verifique se o seu GPS e/ou conexão de dados estão ativos.",Toast.LENGTH_LONG).show();
    	}
    	else{
	    	Intent intent = new Intent(this,br.com.kubic.testedevelocidade.MainActivity3.class);
	    	startActivity(intent);
    	}
    }
}

