package br.com.kubic.testedevelocidade;

import java.io.InputStream;
import java.net.URL;
import android.app.Activity;
import android.app.ProgressDialog;
import android.content.Intent;
import android.net.TrafficStats;
import android.os.AsyncTask;
import android.os.Bundle;
import android.view.View;
import android.widget.TextView;

public class MainActivity3 extends Activity {
    /** Called when the activity is first created. */
    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.results);        
        
        ProgressDialog mProgressDialog;
        mProgressDialog = new ProgressDialog(this);
        mProgressDialog.setMessage("Aguarde 5 segundos para completar o teste de velocidade.");
        mProgressDialog.setIndeterminate(false);
        mProgressDialog.setMax(100);
        mProgressDialog.setProgressStyle(ProgressDialog.STYLE_SPINNER);
    
    	TextView tv = (TextView) findViewById(R.id.tvresult);
    	DownloadTester dt = new DownloadTester(tv,mProgressDialog);    	
    	dt.execute();    	
    }
    
    public void restartTest(View v){
    	Intent intent = getIntent();
    	finish();
    	startActivity(intent);
    }
    
}

class DownloadTester extends AsyncTask<Void, Integer, String>{
	TextView tvInternal;
	ProgressDialog pdInternal;
	
	public DownloadTester(TextView tv, ProgressDialog pd){
		tvInternal = tv;
		pdInternal = pd;
	}

	@Override
	protected String doInBackground(Void... params) {		
		long startTime;
		long endTime;	
		long limitTime;
		long endBytes = 0;		
		long startBytes = 0;
		long result = 0;	
		long downloadBytes = 0;
		float downSpeed = 0;
		float finalSpeed = 0;	
		long difference = 0;
		
		DownloadThread dt = new DownloadThread();
		dt.start();
		startTime = System.nanoTime();
		limitTime = System.nanoTime();
		while (difference<2000000000l){							
			limitTime = System.nanoTime();
			difference = limitTime - startTime;			
		}
		difference = 0;	
		startBytes = TrafficStats.getTotalRxBytes();
		startTime = System.nanoTime();
		limitTime = System.nanoTime();
		while (difference<5000000000l){							
			limitTime = System.nanoTime();
			difference = limitTime - startTime;
			//publishProgress((int)(difference/50000000));
		}
		endTime = System.nanoTime();
		endBytes = TrafficStats.getTotalRxBytes();
		dt.cancel();
		result = endTime - startTime;
		downloadBytes = endBytes - startBytes;
		downSpeed = (downloadBytes / 1000.0f * 8) / (result/1000000000.0f);	
		finalSpeed = downSpeed;			
		
				
					
		String downSpeedString = String.valueOf(finalSpeed);							
		return downSpeedString+" kbps";
	}
	
	protected void onPreExecute(){
		super.onPreExecute();
		pdInternal.show();
	}
	
	protected void onPostExecute(String s){	
		pdInternal.dismiss();
		tvInternal.setText(s);
		return;
	}
	
}

class DownloadThread extends Thread{
	boolean go = true;
	
	public void run(){
		try{
			InputStream is = new URL("http://fisica.ufpr.br/kurumin/kurumin-7.0.iso").openStream();
			byte[] buf = new byte[1024];
			while(go)
				is.read(buf);
			is.close();
		}
		catch(Exception e){
			
		}
	}
	
	public void cancel(){
		go = false;
	}
}

